if(document.getElementById('dat_dispatch1')){

    let d=stringToDate(document.getElementById('dat_dispatch1').value,"yyyy/MM/dd","/");

    let dp = new AirDatepicker('#dat_dispatch',{
        selectedDates: [d], buttons: ['today', 'clear']}
    );
}


function stringToDate(_date,_format,_delimiter) {
    let formatLowerCase=_format.toLowerCase();
    let formatItems=formatLowerCase.split(_delimiter);
    let dateItems=_date.split(_delimiter);

    let monthIndex=formatItems.indexOf("mm");

    let dayIndex=formatItems.indexOf("dd");

    let yearIndex=formatItems.indexOf("yyyy");

    let year = parseInt(dateItems[yearIndex]);
    // adjust for 2 digit year
    if (year < 100) { year += 2000; }
    let month=parseInt(dateItems[monthIndex]);
    month-=1;
    let day=parseInt(dateItems[dayIndex]);
    let formatedDate = new Date(year,month,dateItems[dayIndex]);
    return formatedDate;
}